function removeBotaoAtivo(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("about__tabs__button--is-active")}function esconderTodasAbas(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("about__shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);esconderTodasAbas(),e.classList.add("about__shows__list--is-active"),removeBotaoAtivo(),t.target.classList.add("about__tabs__button--is-active")})});